INSERT INTO action_type (id, title, description) VALUES (101, 'Member transfer', 'The inclusion of new team members fosters knowledge sharing from and towards the new members.');
INSERT INTO action_type (id, title, description) VALUES (102, 'Experienced mentors', 'Assign an experienced mentor to inexperienced team members to collaborate and share knowledge in order to improve on-the-job learning.');
INSERT INTO action_type (id, title, description) VALUES (103, 'Workshops', 'Organize workshops that might be taught by experienced members of the organization or by external consultants for relevant skills that are lacking in the team.');
INSERT INTO action_type (id, title, description) VALUES (104, 'Group dynamics activities', 'Organize group dynamics activities where an instructor suggests an activity analogous to a technical activity.');
INSERT INTO action_type (id, title, description) VALUES (105, 'Collaboration incentives', 'Create incentives for collaboration in the group.');
INSERT INTO action_type (id, title, description) VALUES (106, 'Responsibility training', 'Train team members to take on more responsibility both in trainings and on the job by gradually granting them more responsibility; staff teams with a mix of experiences and new team members to foster on-the-job learning.');
INSERT INTO action_type (id, title, description) VALUES (107, 'Knowledge transfer', 'When a person or several people move out from a group, they must ensure the next person is able to continue the work.');
INSERT INTO action_type (id, title, description) VALUES (108, 'Transfer reduced workload', 'In the situation of knowledge transfer caused by a team member assigned to another project in the same company, reduce workload for the person responsible of transferring the knowledge during the process.');
INSERT INTO action_type (id, title, description) VALUES (109, 'Knowledge registration', 'Capture software architecture knowledge during the development process. This can be supported by web-based approaches, dedicated software architecture knowledge management toolsets, and UML-based solutions. Capturing knowledge should be lightweight and efficient.');
INSERT INTO action_type (id, title, description) VALUES (110, 'Knowledge maintenance', 'The software architecture knowledge captured should be maintained over time and the knowledge history should be tracked.');
INSERT INTO action_type (id, title, description) VALUES (111, 'Knowledge sharing', 'Share the architecture knowledge captured through a web interface or a wiki.');
INSERT INTO action_type (id, title, description) VALUES (112, 'Knowledge reuse', 'Separate generic and project-specific knowledge in order to enable knowledge reuse for generic knowledge.');
