INSERT INTO action_type (id, title, description) VALUES (201, 'Trust belief actions', 'Trust belief for a development methodology relies on an individual trust towards the person that introduces the new methodology (see Trust).');
INSERT INTO action_type (id, title, description) VALUES (202, 'Social influence actions', 'Social influence depends on the perceived usefulness of people known by an individual towards the new methodology (see Performance expectancy).');
INSERT INTO action_type (id, title, description) VALUES (203, 'Reduced interruptions', 'Minimize administrative tasks and infrastructure issues, and reduce interruptions and meetings.');
INSERT INTO action_type (id, title, description) VALUES (204, 'Smaller offices', 'Provide smaller offices to achieve uninterrupted work.');
INSERT INTO action_type (id, title, description) VALUES (205, 'Goals self-reflection', 'Encourage team members to self-reflect about their goals and what are they doing to achieve them in a daily basis.');
INSERT INTO action_type (id, title, description) VALUES (206, 'Salary improvement', 'Improve salary or variable remuneration.');
INSERT INTO action_type (id, title, description) VALUES (207, 'Promotion', 'Improve promotion opportunities.');
