INSERT INTO action_type (id, title, description) VALUES (209, 'Remote work', 'Allow and support working from home.');
INSERT INTO action_type (id, title, description) VALUES (210, 'Meeting rejection', 'Allow the rejection of meeting requests.');
INSERT INTO action_type (id, title, description) VALUES (211, 'Member autonomy', 'Increase team members autonomy.');
INSERT INTO action_type (id, title, description) VALUES (212, 'Methodology benefits', 'Performance expectancy is the most relevant factor for user acceptance. Focus on the benefits that the team will receive if the new methodology is used.');
INSERT INTO action_type (id, title, description) VALUES (213, 'Rotation reduced workload', 'New team members often experience an increase in cognitive workload after a job rotation. Reduce the assigned tasks to new members until they are fully adapted to the job.');
INSERT INTO action_type (id, title, description) VALUES (214, 'Cross-role collaboration', 'Enhance cross-role collaboration.');
