INSERT INTO action_type (id, title, description) VALUES (309, 'Simultaneous projects reduction', 'Reduce the amount of simultaneous projects.');
INSERT INTO action_type (id, title, description) VALUES (310, 'Task complexity balance', 'Balance the cognitive complexity of the tasks among the team.');
