INSERT INTO action_type (id, title, description) VALUES (316, 'Creative tasks distribution', 'Distribute evenly the tasks that involve creating new solutions for new and challenging problems.');
INSERT INTO action_type (id, title, description) VALUES (317, 'Thought-based communication', 'A team with high levels of need for cognition will respond better to thought-based communication, rather than feeling-based communication.');
