INSERT INTO action_type (id, title, description) VALUES (401, 'Empathetic team', 'Encourage the team to practice empathy.');
INSERT INTO action_type (id, title, description) VALUES (402, 'Transparent tasks', 'Ensure that all the necessary information related to the team''s tasks is always provided and transparent.');
INSERT INTO action_type (id, title, description) VALUES (403, 'Supportive team', 'Foster the creation of a healthy and safe environment. The team should be a space where team members are open to talk about private issues, emotions and feelings.');
INSERT INTO action_type (id, title, description) VALUES (404, 'Non-collaborative people replacement', 'Replace non-collaborative people in key positions.');
INSERT INTO action_type (id, title, description) VALUES (405, 'Collaboration frequency', 'Increase collaboration frequency between team members. Real collaboration means that different team members work in the same task. Daily team meetings where team members can support each other enhance collaboration.');
