INSERT INTO action_type (id, title, description) VALUES (408, 'Change expectations alignment', 'Align expectations with the goals of change.');
INSERT INTO action_type (id, title, description) VALUES (409, 'Continuous improvement culture', 'Encourage team members to look for ways to improve things. It is necessary that the company experiences a cultural shift where change is accepted, where employees and managers feel empowered to seek out and apply new ways to improve things and where there is courage to keep going when setbacks occur.');
INSERT INTO action_type (id, title, description) VALUES (410, 'Expert allocation', 'Allocate people experienced with the new methodology to the team.');
