INSERT INTO action_type (id, title, description) VALUES (601, 'Product teams', 'Organize development teams in product teams. Product teams are usually small and promote skills over roles, self-organization, end-to-end product vision, cross-functionality (multidisciplinary), and have a single leadership over multiple management.');
INSERT INTO action_type (id, title, description) VALUES (602, 'Dependency removal', 'Remove dependencies with other teams composing the team of T-shaped members, i.e., people experienced in different areas that collaborate with each other, in a way that every skill-set required to have an end-to-end product vision is present in at least one member.');
INSERT INTO action_type (id, title, description) VALUES (603, 'Horizontal DevOps teams', 'Instantiate Horizontal DevOps teams through centers of excellence, chapters, and/or platform teams. Centers of excellence and platform teams are external teams that support product teams with platform servicing, and chapters designate people with DevOps skills across different product teams.');
INSERT INTO action_type (id, title, description) VALUES (604, 'Team product ownership', 'Encourage the team to acknowledge ownership over the product that is being developed. They should be personally responsible for the outcomes related to it.');
INSERT INTO action_type (id, title, description) VALUES (605, 'Cognitive recruitment', 'Provide cognitive reasoning in recruiting messages. Software professionals are more receptive to cognitive (i.e., thought-based) recruiting messages.');
INSERT INTO action_type (id, title, description) VALUES (606, 'Unemotional recruitment', 'Avoid devoting resources to create an emotional link between the organization and candidates.');
INSERT INTO action_type (id, title, description) VALUES (607, 'Benefit recruitment', 'Focus the recruitment process on communicating upon reasons why the organization is attractive to future employees. Advertise work-life benefits.');
INSERT INTO action_type (id, title, description) VALUES (608, 'Cognitive candidates', 'Actively look for people that enjoy effortful thinking. People that enjoy effortful thinking (need for cognition) are more probable to have a better performance. People that include high conscientiousness, openness to experience, humility-honesty, and low emotionality, are more probable to also have this personality trait.');
INSERT INTO action_type (id, title, description) VALUES (609, 'Financial resources allocation', 'Allocate financial resources that may be used to hire consultants, train people, and assume the temporary deceleration of production sometimes experienced.');
INSERT INTO action_type (id, title, description) VALUES (610, 'Methodology training', 'Let team members participate in development programs that train them in coping with the methodology and its challenges.');
INSERT INTO action_type (id, title, description) VALUES (611, 'Face-to-face communication', 'Make sure that face-to-face communication is available for every team member.');
INSERT INTO action_type (id, title, description) VALUES (612, 'Peaceful environment', 'Discourage attacks against team members that might be caused by poor advices or inadequate explanations.');
INSERT INTO action_type (id, title, description) VALUES (613, 'Silos removal', 'Remove organizational and cultural silos. Any organizational structures that separate skill-sets or unable team self-organization hinder DevOps adoption.');
INSERT INTO action_type (id, title, description) VALUES (614, 'Member empowerment', 'Increase the abilities, responsibilities, formal authority, effective capacity and involvement of broadly skilled frontline workers in problem solving, decision making and continuous improvement.');
